# frozen_string_literal: true

class BooknowController < PublicWebsiteController
  require 'uri'
  layout 'booknow'
  skip_before_action :verify_authenticity_token

  def landing_page
    render layout: 'public_website'
  end

  def search
    redirect_to '/', status: :moved_permanently, format: :json
  end

  def cojilio_results; end

  def results
    return if params[:lat].blank? && params[:lng].blank?
    results_response = `curl -X GET \
    '#{ENV.fetch('GLOSS_GENIUS_API_URL', nil)}search?#{gloss_genius_search_query_string}' \
    -H 'api_key: #{ENV.fetch('GLOSS_GENIUS_API_KEY', nil)}' \
    -H 'device_id: #{params[:fingerprint]}'`

    # results_response = `curl -X GET http://httpstat.us/500`

    # p "#{results_response}"

    begin
      
      @professionals = []
      @professional_results = JSON.parse(results_response)
      @date = params[:date] ? DateTime.parse(params[:date]) : DateTime.now
      # @locations = Location.near([params[:lat].to_f, params[:lng].to_f], 11)
      @locations = Location.where(id: get_location_id(@professional_results))

      @professional_results.each do |professional|
        sola_professional = professional['org_user_id'] ? Stylist.find_by(id: professional['org_user_id']) : nil
        location = sola_professional ? sola_professional.location : nil
        if location && sola_professional.open?
          # p "professional['business_address']=#{professional['business_address']}, #{location.full_address}"
          professional['business_address'] = location.full_address
          professional['location_latitude'] = location.latitude
          professional['location_longitude'] = location.longitude
          @professionals << professional
        else
          # do not add them - missing ids
        end
      end

      if params[:location_id].present?
        @location = Location.find_by(id: params[:location_id])
      end

      respond_to do |format|
        # p "format=#{format}"
        format.html
        format.json
      end
    rescue StandardError => e
      NewRelic::Agent.notice_error(e)
      Rollbar.error(e)
      respond_to do |format|
        # p "format=#{format}"
        format.html
        format.json { render :json => {status: :moved_permanently} }
      end
      # redirect_to booknow_search_path(date: params[:date], location: params[:location], lat: params[:lat], lng: params[:lng], query: params[:query]), flash: { error: 'There was a problem with your search. Please try again.' }, status: :moved_permanently
      
      @date = DateTime.parse(params[:date]) || DateTime.now
    end
  end

  def booking_complete
    # p "params[:time]=#{params[:time]}"
    time_json = JSON.parse(params[:time])
    # p "time_json=#{time_json}"
    start_time = time_json['start'].to_datetime.utc.to_s
    end_time = time_json['end'].to_datetime.utc.to_s
    # p "start_time=#{start_time}, end_time=#{end_time}"
    @time_json = {
      start: start_time,
      end:   end_time
    }
  end

  def save_booking
    # p "save_booking..."

    bnb = BookNowBooking.create({
                                  location_id:        params[:location_id],
                                  stylist_id:         params[:stylist_id],
                                  time_range:         params[:time_range],
                                  services:           params[:services],
                                  query:              params[:query],
                                  booking_user_name:  params[:booking_user_name],
                                  booking_user_phone: params[:booking_user_phone],
                                  booking_user_email: params[:booking_user_email],
                                  referring_url:      params[:referring_url],
                                  total:              params[:total]
                                })

    # p "bnb=#{bnb.inspect}"

    head :ok
  end

