SELECT s.id, s.name AS stylist_name, s.url_name, s.email_address, s.phone_number, s.studio_number, s.work_hours, s.website_url, s.booking_url, s.business_name, s.status, s.has_sola_genius_account, s.sola_genius_enabled, s.sola_pro_platform, s.sola_pro_version,  s.created_at, s.updated_at, l.name AS location_name, l.city, l.state FROM stylists AS s, locations AS l WHERE s.location_id = l.id